#
# Structure for table "ddsc_car_dd"
#

DROP TABLE IF EXISTS `ddsc_car_dd`;
CREATE TABLE `ddsc_car_dd` (
  `did` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) DEFAULT NULL COMMENT '用户id',
  `gid` int(11) DEFAULT NULL COMMENT '商品id',
  `sid` int(11) DEFAULT NULL COMMENT '商品规格',
  `price` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '商品单价',
  `num` int(11) DEFAULT NULL COMMENT '商品数量',
  `zprice` decimal(10,2) DEFAULT NULL COMMENT '商品总价',
  `gname` varchar(255) DEFAULT NULL COMMENT '商品名称',
  `sname` varchar(255) DEFAULT NULL COMMENT '规格名称',
  `simage` varchar(255) DEFAULT NULL COMMENT '商品图片',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '支付状态 0待支付 1已支付 2已发货 3已收货 4已完成',
  `code` varchar(255) DEFAULT NULL COMMENT '订单编号',
  `pay` text COMMENT '总订单编号',
  `time` char(20) DEFAULT NULL COMMENT '提交时间',
  `aid` int(11) DEFAULT NULL COMMENT '收货地址id',
  `content` text COMMENT '订单留言',
  `express` varchar(255) DEFAULT NULL COMMENT '快递公司',
  `number` varchar(255) DEFAULT NULL COMMENT '快递单号',
  `fa_time` char(20) DEFAULT NULL COMMENT '发货时间',
  `cencal` text COMMENT '退货原因',
  `t_time` char(20) DEFAULT NULL COMMENT '申请退货时间',
  `shou_time` varchar(255) DEFAULT NULL COMMENT '收货时间',
  `remarks` varchar(255) DEFAULT NULL COMMENT '退货/退款备注',
  `state` tinyint(3) NOT NULL DEFAULT '0' COMMENT '退货进度 0已提交 1已成功 2已驳回',
  `old_status` tinyint(3) NOT NULL DEFAULT '0' COMMENT '申请退货前状态',
  `fu_time` varchar(220) DEFAULT NULL COMMENT '付款时间',
  `shop_id` int(11) NOT NULL DEFAULT '0' COMMENT '商户id',
  PRIMARY KEY (`did`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='订单表';

