  `fid` int(11) DEFAULT NULL COMMENT '所属分类id',
  `type` varchar(255) DEFAULT NULL COMMENT '商品型号',
  `code` varchar(255) DEFAULT NULL COMMENT '商品货号',
  `specs` varchar(255) DEFAULT NULL COMMENT '包装规格',
  `canal` tinyint(3) NOT NULL DEFAULT '0' COMMENT '供货渠道 1代理商 2客服',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

