# Structure for table "ddsc_lb_place"
#

DROP TABLE IF EXISTS `ddsc_lb_place`;
CREATE TABLE `ddsc_lb_place` (
  `pl_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '轮播id',
  `pl_name` varchar(255) DEFAULT NULL COMMENT '位置名称',
  PRIMARY KEY (`pl_id`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='广告位';

#
# Data for table "ddsc_lb_place"
#

INSERT INTO `ddsc_lb_place` VALUES (1,'商城banner'),(2,'商城中部banner'),(47,'服务保障');

#
# Structure for table "ddsc_money_log"
#

DROP TABLE IF EXISTS `ddsc_money_log`;
CREATE TABLE `ddsc_money_log` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL DEFAULT '0' COMMENT '用户id',
  `money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '金额',
  `type` tinyint(3) NOT NULL DEFAULT '0' COMMENT '类型 1增加 2减少',
  `oper` varchar(255) DEFAULT NULL COMMENT '操作人',
  `content` varchar(255) DEFAULT NULL COMMENT '充值备注',
  `time` varchar(255) DEFAULT NULL COMMENT '充值时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=4 DEFAULT CHARSET=utf8 COMMENT='充值日志';

#
# Data for table "ddsc_money_log"
#

/*!40000 ALTER TABLE `ddsc_money_log` DISABLE KEYS */;
INSERT INTO `ddsc_money_log` VALUES (1,3,100.00,1,'admin','123','1575597459'),(2,3,10.00,2,'admin','','1575597472'),(3,4,10.00,1,'admin','','1575620497');
