#
# Structure for table "ddsc_user"
#

DROP TABLE IF EXISTS `ddsc_user`;
CREATE TABLE `ddsc_user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT,
  `nickname` varchar(255) CHARACTER SET utf8mb4 DEFAULT NULL COMMENT '昵称',
  `image` text COMMENT '头像',
  `phone` varchar(20) NOT NULL DEFAULT '',
  `pwd` varchar(255) DEFAULT NULL COMMENT '密码',
  `money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '用户余额',
  `type` tinyint(3) NOT NULL DEFAULT '1' COMMENT '用户类型 1代理商 2客服',
  `time` varchar(255) DEFAULT NULL COMMENT '添加时间',
  `status` tinyint(3) NOT NULL DEFAULT '1' COMMENT '1启用 2禁用',
  `addr` varchar(255) DEFAULT NULL COMMENT '代理商区域',
  `recharge_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '充值金额',
  `consume_money` decimal(10,2) NOT NULL DEFAULT '0.00' COMMENT '消费金额',
  PRIMARY KEY (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 ROW_FORMAT=COMPACT COMMENT='用户列表';

